"""backprop_network.py"""import randomimport numpy as npimport mathimport matplotlib.pyplot as pltfrom matplotlib.ticker import (MultipleLocator, FormatStrFormatter,                               AutoMinorLocator)import matplotlib.patches as mpatchesdb_0_norm = []db_1_norm = []db_2_norm = []db_3_norm = []db_4_norm = []class Network(object):    def __init__(self, sizes):        """The list ``sizes`` contains the number of neurons in the        respective layers of the network.  For example, if the list        was [2, 3, 1] then it would be a three-layer network, with the        first layer containing 2 neurons, the second layer 3 neurons,        and the third layer 1 neuron.  The biases and weights for the        network are initialized randomly, using a Gaussian        distribution with mean 0, and variance 1.  Note that the first        layer is assumed to be an input layer, and by convention we        won't set any biases for those neurons, since biases are only        ever used in computing the outputs from later layers."""        self.num_layers = len(sizes)        self.sizes = sizes        self.biases = [np.random.randn(y, 1) for y in sizes[1:]]        self.weights = [np.random.randn(y, x)                        for x, y in zip(sizes[:-1], sizes[1:])]    def SGD(self, training_data, epochs, mini_batch_size, learning_rate,            test_data):        """Train the neural network using mini-batch stochastic        gradient descent.  The ``training_data`` is a list of tuples        ``(x, y)`` representing the training inputs and the desired        outputs.  """        print("Initial test accuracy: {0}".format(self.one_label_accuracy(test_data)))        n = len(training_data)        test_accuracy_list = []        train_accuracy_list = []        train_loss_list = []        for j in range(epochs):            print("Epoch {0}".format(j))            random.shuffle(list(training_data))            mini_batches = [                training_data[k:k+mini_batch_size]                for k in range(0, n, mini_batch_size)]            for mini_batch in mini_batches:                self.update_mini_batch(mini_batch, learning_rate)            # test_accuracy_list.append(self.one_label_accuracy(test_data))            # train_accuracy_list.append(self.one_hot_accuracy(training_data))            # train_loss_list.append(self.loss(training_data))            # print ("Epoch {0} test accuracy: {1}".format(j, self.one_label_accuracy(test_data)))            # print ("Epoch {0} train accuracy: {1}".format(j, self.one_hot_accuracy(training_data)))            # print ("Epoch {0} train loss: {1}".format(j, self.loss(training_data)))        # self.create_accuracy_plot(np.arange(epochs) + 1, test_accuracy_list, 'Test Accuracy', '#f28f55')        # self.create_accuracy_plot(np.arange(epochs) + 1, train_accuracy_list, 'Train Accuracy', '#55c6f2')        # self.create_accuracy_plot(np.arange(epochs) + 1, train_loss_list, 'Train Loss', '#fffb0f')        # print('Test Accuracy: {}'.format(self.one_label_accuracy(test_data)))        self.create_db_plot(np.arange(epochs) + 1)    def create_db_plot(self, x_data):        fig, ax = plt.subplots()        plt.plot(db_0_norm, color='#000099', label='layer 0')        plt.plot(db_1_norm, color='#0000CC', label='layer 1')        plt.plot(db_2_norm, color='#0000FF', label='layer 2')        plt.plot(db_3_norm, color='#3333FF', label='layer 3')        plt.plot(db_4_norm, color='#6666FF', label='layer 4')        plt.xlabel('Epoch')        plt.xticks(x_data)        ax.xaxis.set_major_locator(MultipleLocator(5))        plt.ylabel('db i=0..4')        db_0_patch = mpatches.Patch(color='#000099', label='dl/db_0')        db_1_patch = mpatches.Patch(color='#0000CC', label='dl/db_1')        db_2_patch = mpatches.Patch(color='#0000FF', label='dl/db_2')        db_3_patch = mpatches.Patch(color='#3333FF', label='dl/db_3')        db_4_patch = mpatches.Patch(color='#6666FF', label='dl/db_4')        plt.legend(handles=[db_0_patch, db_1_patch, db_2_patch, db_3_patch, db_4_patch])        plt.savefig('results/section_2d.png')    def create_accuracy_plot(self, x_data, y_data, y_axis_label, color):        fig, ax = plt.subplots()        plt.plot(x_data, y_data, color=color)        plt.xticks(x_data)        ax.xaxis.set_major_locator(MultipleLocator(5))        plt.xlabel('Epochs')        plt.ylabel(y_axis_label)        plt.savefig('results/section_2b_{}.png'.format(y_axis_label))    def update_mini_batch(self, mini_batch, learning_rate):        """Update the network's weights and biases by applying        stochastic gradient descent using backpropagation to a single mini batch.        The ``mini_batch`` is a list of tuples ``(x, y)``."""        nabla_b = [np.zeros(b.shape) for b in self.biases]        nabla_w = [np.zeros(w.shape) for w in self.weights]        for x, y in mini_batch:            delta_nabla_b, delta_nabla_w = self.backprop(x, y)            nabla_b = [nb+dnb for nb, dnb in zip(nabla_b, delta_nabla_b)]            nabla_w = [nw+dnw for nw, dnw in zip(nabla_w, delta_nabla_w)]        self.weights = [w - (learning_rate / len(mini_batch)) * nw                        for w, nw in zip(self.weights, nabla_w)]        self.biases = [b - (learning_rate / len(mini_batch)) * nb                       for b, nb in zip(self.biases, nabla_b)]        db_0_norm.append(np.linalg.norm(nabla_b[0]) / len(mini_batch))        db_1_norm.append(np.linalg.norm(nabla_b[1]) / len(mini_batch))        db_2_norm.append(np.linalg.norm(nabla_b[2]) / len(mini_batch))        db_3_norm.append(np.linalg.norm(nabla_b[3]) / len(mini_batch))        db_4_norm.append(np.linalg.norm(nabla_b[4]) / len(mini_batch))    def calc_v_list(self, x ,y):        result = []        for weight, bias in zip(self.weights, self.biases):            vector = np.dot(weight,x)            vector += bias            result.append(vector)            x = sigmoid(vector)        return result    def backprop(self, x, y):        """The function receives as input a 784 dimensional         vector x and a one-hot vector y.        The function should return a tuple of two lists (db, dw)         as described in the assignment pdf. """        layers_num = len(self.weights)        v_list = self.calc_v_list(x, y)  # list of vectors after dot product [v1, v2]        sigmoid_derivated_of_v_list = [sigmoid_derivative(layer) for layer in v_list]  # [h'(v1), h'(v2)]        z_list = [x] + [sigmoid(layer) for layer in v_list]  # [z0=x, z1=h(v1), z2=h(v2)]        db = [None] * layers_num        db[layers_num - 1] = self.loss_derivative_wr_output_activations(v_list[-1], y)  # the derivative of last layer        # start back propagation for db        for i in range(layers_num - 2, -1, -1):  # go backward - start from the second from last object            db[i] = np.dot(self.weights[i + 1].transpose(), db[i + 1]) * sigmoid_derivated_of_v_list[i]        # dw can be calculated forward as it is as db * z_i        dw = [None] * layers_num        for i in range(layers_num):            dw[i] = np.dot(db[i], z_list[i].T)        return db, dw    def one_label_accuracy(self, data):        """Return accuracy of network on data with numeric labels"""        output_results = [(np.argmax(self.network_output_before_softmax(x)), y)         for (x, y) in data]        return sum(int(x == y) for (x, y) in output_results)/float(len(data))    def one_hot_accuracy(self,data):        """Return accuracy of network on data with one-hot labels"""        output_results = [(np.argmax(self.network_output_before_softmax(x)), np.argmax(y))                          for (x, y) in data]        return sum(int(x == y) for (x, y) in output_results) / float(len(data))    def network_output_before_softmax(self, x):        """Return the output of the network before softmax if ``x`` is input."""        layer = 0        for b, w in zip(self.biases, self.weights):            if layer == len(self.weights) - 1:                x = np.dot(w, x) + b            else:                x = sigmoid(np.dot(w, x)+b)            layer += 1        return x    def loss(self, data):        """Return the loss of the network on the data"""        loss_list = []        for (x, y) in data:            net_output_before_softmax = self.network_output_before_softmax(x)            net_output_after_softmax = self.output_softmax(net_output_before_softmax)            loss_list.append(np.dot(-np.log(net_output_after_softmax).transpose(),y).flatten()[0])        return sum(loss_list) / float(len(data))    def output_softmax(self, output_activations):        """Return output after softmax given output before softmax"""        output_exp = np.exp(output_activations)        return output_exp/output_exp.sum()    def loss_derivative_wr_output_activations(self, output_activations, y):        """Return derivative of loss with respect to the output activations before softmax"""        return self.output_softmax(output_activations) - ydef sigmoid(z):    """The sigmoid function."""    return 1.0/(1.0+np.exp(-z))def sigmoid_derivative(z):    """Derivative of the sigmoid function."""    return sigmoid(z)*(1-sigmoid(z))